import React from 'react';

// Define a type for the cart item structure
interface CartItem {
  name: string;
  amount: number;
  totalPrice: number;
}

interface CartComponentProps {
  cartItems: CartItem[];
  totalSum: number;
  updateCart: (newCart: CartItem[]) => void;
}

const CartComponent: React.FC<CartComponentProps> = ({ cartItems, totalSum, updateCart }) => {
  const handleRemoveItem = (name: string) => {
    let cart: CartItem[] = JSON.parse(localStorage.getItem('cart') || '[]');
    cart = cart.filter((item) => item.name !== name);
    updateCart(cart); // Update the cart using the passed prop
  };

  return (
    <div>
      <h2>Shopping Cart</h2>
      {cartItems.length === 0 ? (
        <p>Your cart is empty</p>
      ) : (
        <ul>
          {cartItems.map((item) => (
            <li key={item.name}>
              <h3>{item.name}</h3>
              <p>Amount: {item.amount}</p>
              <p>Price: ${item.totalPrice}</p>
              <button onClick={() => handleRemoveItem(item.name)}>Remove</button>
            </li>
          ))}
        </ul>
      )}
      <div className="total-sum">
        <h3>Total: ${totalSum.toFixed(2)}</h3>
      </div>
    </div>
  );
};

export default CartComponent;
