// ProductsComponent.tsx
import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom'; // To access route params
import { Product } from './ShopModels';
import AmountControl from './AmountControl';

const ProductsComponent: React.FC = () => {
  const [products, setProducts] = useState<Product[]>([]);
  const { id } = useParams<{ id: string }>();  // Get category id from URL

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await fetch(`http://127.0.0.1:8000/products?category=${id}`); // Include category ID in the URL
        if (response.ok) {
          const data = await response.json();
          setProducts(data);
        } else {
          console.error('Failed to fetch products');
        }
      } catch (error) {
        console.error('Error fetching products:', error);
      }
    };

    if (id) {
      fetchProducts();
    }
  }, [id]);  // Run the effect whenever the category id changes

  const handleAddToCart = (name: string, price: number) => {
    let cart: CartItem[] = JSON.parse(localStorage.getItem('cart') || '[]');
    const existingItem = cart.find((item) => item.name === name);
  
    if (existingItem) {
      existingItem.amount += 1;
      existingItem.totalPrice += price;
    } else {
      cart.push({
        name,
        amount: 1,
        totalPrice: price,
      });
    }
  
    localStorage.setItem('cart', JSON.stringify(cart));
  
    // Update the cart state immediately after adding the item
    loadCartItems(); // Make sure to update the cart UI
  };

  return (
    <div>
      <h2>Products</h2>
      <ul>
        {products.map((product) => (
          <li key={product.id}>
            <h3>{product.name}</h3>
            <p>Price: ${product.price}</p>
            {/* Pass product name and price as props */}
            <AmountControl productName={product.name} productPrice={product.price} />
          </li>
        ))}
      </ul>
    </div>
  );
};

export default ProductsComponent;
