import React, { useEffect, useState } from 'react';

// Define a type for the cart item structure
interface CartItem {
  name: string;
  amount: number;
  totalPrice: number;
}

const CartComponent: React.FC = () => {
  const [cartItems, setCartItems] = useState<CartItem[]>([]);
  const [totalSum, setTotalSum] = useState<number>(0);

  useEffect(() => {
    const loadCartItems = () => {
      // Get the cart from local storage
      const cart: CartItem[] = JSON.parse(localStorage.getItem('cart') || '[]');

      // Group cart items by product name and sum their prices
      const groupedItems: { [key: string]: CartItem } = cart.reduce((acc, item) => {
        if (acc[item.name]) {
          acc[item.name].amount += item.amount;
          acc[item.name].totalPrice += item.totalPrice;
        } else {
          acc[item.name] = { ...item };
        }
        return acc;
      }, {} as { [key: string]: CartItem });  // Explicitly type the accumulator

      // Convert the grouped object to an array of items
      const updatedCartItems = Object.values(groupedItems);

      // Calculate the total sum of the cart
      const total = updatedCartItems.reduce((sum, item) => sum + item.totalPrice, 0);

      setCartItems(updatedCartItems);
      setTotalSum(total); // This should now correctly recognize the total as a number
    };

    loadCartItems();
  }, []);  // Run only once when the component is mounted

  const handleRemoveItem = (name: string) => {
    // Remove product by name from localStorage
    let cart: CartItem[] = JSON.parse(localStorage.getItem('cart') || '[]');
    cart = cart.filter((item) => item.name !== name);
    localStorage.setItem('cart', JSON.stringify(cart));

    // Update the cart state after removal
    setCartItems(cart);
  };

  return (
    <div>
      <h2>Shopping Cart</h2>
      {cartItems.length === 0 ? (
        <p>Your cart is empty</p>
      ) : (
        <ul>
          {cartItems.map((item) => (
            <li key={item.name}>
              <h3>{item.name}</h3>
              <p>Amount: {item.amount}</p>
              <p>Price: ${item.totalPrice}</p>
              <button onClick={() => handleRemoveItem(item.name)}>Remove</button>
            </li>
          ))}
        </ul>
      )}
      <div className="total-sum">
        <h3>Total: ${totalSum.toFixed(2)}</h3>
      </div>
    </div>
  );
};

export default CartComponent;
