import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom'; // To access route params
import { Product } from './ShopModels';
import AmountControl from './AmountControl';

const ProductsComponent: React.FC = () => {
  const [products, setProducts] = useState<Product[]>([]);
  const { id } = useParams<{ id: string }>();  // Get category id from URL
  const [cart, setCart] = useState<{ [key: string]: { name: string, amount: number, totalPrice: number } }>({});

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await fetch(`http://127.0.0.1:8000/products?category=${id}`);
        if (response.ok) {
          const data = await response.json();
          setProducts(data);
        } else {
          console.error('Failed to fetch products');
        }
      } catch (error) {
        console.error('Error fetching products:', error);
      }
    };

    if (id) {
      fetchProducts();
    }
  }, [id]);

  const addToCart = (productName: string, productPrice: number, amount: number) => {
    setCart((prevCart) => {
      const newCart = { ...prevCart };
      if (newCart[productName]) {
        newCart[productName].amount += amount;
        newCart[productName].totalPrice += productPrice * amount;
      } else {
        newCart[productName] = { name: productName, amount, totalPrice: productPrice * amount };
      }
      return newCart;
    });
  };

  return (
    <div>
      <h2>Products</h2>
      <ul>
        {products.map((product) => (
          <li key={product.id}>
            <h3>{product.name}</h3>
            <p>Price: ${product.price}</p>
            {/* Pass product name, price, and the addToCart function as props */}
            <AmountControl 
              productName={product.name} 
              productPrice={product.price} 
              addToCart={addToCart}
            />
          </li>
        ))}
      </ul>
    </div>
  );
};

export default ProductsComponent;
