import React, { useState, useEffect } from 'react';
import CategoriesComponent from './bodyComponents/Categories'; // Import Categories component
import ProductsComponent from './bodyComponents/Products'; // Import Products component
import Login from './headerComponents/Login'; // Import the Login component
import Logout from './headerComponents/Logout'; // Import the Logout component

const App: React.FC = () => {
  const [isLoggedIn, setIsLoggedIn] = useState<boolean>(false);

  useEffect(() => {
    const token = localStorage.getItem('token');
    setIsLoggedIn(!!token); // If token exists, the user is logged in
  }, []);

  return (
    <div className="App">
      <header>
        <div className="left">
          {isLoggedIn ? (
            <Logout setIsLoggedIn={setIsLoggedIn} />
          ) : (
            <Login setIsLoggedIn={setIsLoggedIn} />
          )}
        </div>
      </header>

      <main>
        {/* Conditionally render the Categories and Products components */}
        <CategoriesComponent />
        <ProductsComponent />
      </main>
    </div>
  );
};

export default App;
