// CartComponent.tsx
import React from 'react';

interface CartItem {
  id: number;
  name: string;
  price: number;
  quantity: number;
}

interface CartComponentProps {
  cartItems: CartItem[];
  increaseQuantity: (id: number) => void;
  decreaseQuantity: (id: number) => void;
  removeItem: (id: number) => void;
  clearCart: () => void; // New prop to clear the cart
}

const CartComponent: React.FC<CartComponentProps> = ({ cartItems, increaseQuantity, decreaseQuantity, removeItem, clearCart }) => {
  // Calculate total price with 2 decimal places
  const totalPrice = cartItems.reduce((total, item) => total + item.price * item.quantity, 0).toFixed(2);

  return (
    <div>
      <h2>Cart</h2>
      <ul>
        {cartItems.map((item) => (
          <li key={item.id}>
            <h3>{item.name}</h3>
            <p>Price: ${item.price}</p>
            <p>Quantity: {item.quantity}</p>
            <div>
              <button onClick={() => decreaseQuantity(item.id)} disabled={item.quantity === 1}>-</button>
              <button onClick={() => increaseQuantity(item.id)}>+</button>
              <button onClick={() => removeItem(item.id)}>Remove</button>
            </div>
          </li>
        ))}
      </ul>
      <h3>Total: ${totalPrice}</h3>
      <button onClick={clearCart}>Clear Cart</button> {/* Button to clear cart */}
    </div>
  );
};

export default CartComponent;
