import React, { useEffect, useState } from 'react';
import jwt_decode from 'jwt-decode';  // Ensure this import is correct

interface LogoutProps {
    setIsLoggedIn: (status: boolean) => void;
}

interface TokenPayload {
    username: string;  // Adjust according to your token structure
}

const Logout: React.FC<LogoutProps> = ({ setIsLoggedIn }) => {
    const [username, setUsername] = useState<string | null>(null);

    useEffect(() => {
        const token = localStorage.getItem('token');
        if (token) {
            try {
                // Decode the token to get the username
                const decoded: TokenPayload = jwt_decode(token); // Call jwt_decode function here
                setUsername(decoded.username); // Store the username
            } catch (error) {
                console.error('Failed to decode token:', error);
            }
        }
    }, []);

    const handleLogout = () => {
        localStorage.removeItem('token');  // Clear the token
        setIsLoggedIn(false);  // Update login state
    };

    return (
        <div>
            {username && <p>Welcome, {username}!</p>}  {/* Display username if available */}
            <button onClick={handleLogout}>Logout</button>
        </div>
    );
};

export default Logout;
