// App.tsx
import React, { useEffect, useState } from 'react';
import Login from './headerComponents/Login'; // Assuming you already have a Login component
import Logout from './headerComponents/Logout';

const App: React.FC = () => {
  const [isLoggedIn, setIsLoggedIn] = useState<boolean>(false);

  useEffect(() => {
    // Check if token exists in local storage
    const token = localStorage.getItem('token');
    setIsLoggedIn(!!token); // Set the state based on the presence of a token
  }, []);

  return (
    <div className="App">
      <header>
        <div className="left">
          {isLoggedIn ? <Logout /> : <Login />}
        </div>
      </header>

      {/* The rest of your app */}
    </div>
  );
};

export default App;
