import React, { useState, useEffect } from 'react';
import Login from './headerComponents/Login'; // Import the Login component
import Logout from './headerComponents/Logout'; // Import the Logout component

const App: React.FC = () => {
    const [isLoggedIn, setIsLoggedIn] = useState<boolean>(false);
    const [message, setMessage] = useState<string>(''); // Message state for login/logout feedback

    useEffect(() => {
        const token = localStorage.getItem('token');
        setIsLoggedIn(!!token); // If token exists, the user is logged in
    }, []);

    return (
        <div className="App">
            <header>
                <div className="left">
                    {/* Conditionally render Login or Logout component */}
                    {isLoggedIn ? (
                        <Logout setIsLoggedIn={setIsLoggedIn} setMessage={setMessage} />
                    ) : (
                        <Login setIsLoggedIn={setIsLoggedIn} />
                    )}
                </div>
                {/* Show message if it exists */}
                {message && <h2>{message}</h2>}
            </header>

            {/* Your other app content */}
        </div>
    );
};

export default App;
