import React, { useState } from 'react';

const AmountControl: React.FC<{ productName: string, productPrice: number }> = ({ productName, productPrice }) => {
  // State to manage the amount, initially set to 0
  const [amount, setAmount] = useState<number>(0);

  // Function to handle increase
  const increaseAmount = () => {
    setAmount(amount + 1);
  };

  // Function to handle decrease, with a check to ensure it doesn't go below 0
  const decreaseAmount = () => {
    if (amount > 0) {
      setAmount(amount - 1);
    }
  };


  return (
    <div className="amount-control">
      <div className="amount-display">
        <h3>Amount: {amount}</h3>
      </div>
      <div className="buttons">
        <button
          onClick={decreaseAmount}
          disabled={amount === 0} // Disable minus button when amount is 0
        >
          -
        </button>
        <button onClick={increaseAmount}>+</button>
      </div>
      <div className="add-to-cart">
        <button
          onClick={addToCart}
          disabled={amount <= 0} // Disable "Add to Cart" if amount is 0 or less
        >
          Add to Cart
        </button>
      </div>
    </div>
  );
};

export default AmountControl;
