import React, { useEffect, useState } from 'react';
import { jwtDecode } from 'jwt-decode';  // Corrected import for named export

interface LogoutProps {
    setIsLoggedIn: (status: boolean) => void;
    setUsername: (username: string | null) => void;  // Add setUsername to reset the username on logout
}

interface TokenPayload {
    username: string;
}

const Logout: React.FC<LogoutProps> = ({ setIsLoggedIn, setUsername }) => {
    const [username, setUsernameState] = useState<string | null>(null);

    useEffect(() => {
        const token = localStorage.getItem('token');
        if (token) {
            try {
                // Decode the token to get the username
                const decoded: TokenPayload = jwtDecode(token);
                setUsernameState(decoded.username);  // Store the username
            } catch (error) {
                console.error('Failed to decode token:', error);
            }
        }
    }, []);

    const handleLogout = () => {
        localStorage.removeItem('token');  // Clear the token
        localStorage.removeItem('username');  // Clear the username
        setIsLoggedIn(false);  // Update login state
        setUsername(null);  // Reset the username state
    };

    return (
        <div>
            <button onClick={handleLogout}>Logout</button>
            <h2>{username && <p>Hello, {username}!</p>}  {/* Display username if available */}</h2>
        </div>
    );
};

export default Logout;
