import React, { useState } from 'react';

const AmountControl: React.FC<{ productName: string, productPrice: number, addToCart: (productName: string, productPrice: number, amount: number) => void }> = ({ productName, productPrice, addToCart }) => {
  const [amount, setAmount] = useState<number>(0);

  const increaseAmount = () => {
    setAmount(amount + 1);
  };

  const decreaseAmount = () => {
    if (amount > 0) {
      setAmount(amount - 1);
    }
  };

  const handleAddToCart = () => {
    if (amount > 0) {
      addToCart(productName, productPrice, amount);
    } else {
      alert('Please select an amount greater than 0');
    }
  };

  return (
    <div className="amount-control">
      <div className="amount-display">
        <h3>Amount: {amount}</h3>
      </div>
      <div className="buttons">
        <button onClick={decreaseAmount} disabled={amount === 0}>
          -
        </button>
        <button onClick={increaseAmount}>+</button>
      </div>
      <div className="add-to-cart">
        <button onClick={handleAddToCart} disabled={amount <= 0}>
          Add to Cart
        </button>
      </div>
    </div>
  );
};

export default AmountControl;
