import React, { useState, useEffect } from 'react';
import Login from './headerComponents/Login'; // Import the Login component
import Logout from './headerComponents/Logout'; // Import the Logout component

const App: React.FC = () => {
    const [isLoggedIn, setIsLoggedIn] = useState<boolean>(false);

    useEffect(() => {
        const token = localStorage.getItem('token');
        setIsLoggedIn(!!token); // If token exists, the user is logged in
    }, []);

    return (
        <div className="App">
            <header>
                <div className="left">
                    {/* Conditionally render Login or Logout component */}
                    {isLoggedIn ? (
                        <Logout setIsLoggedIn={setIsLoggedIn}/>
                    ) : (
                        <Login setIsLoggedIn={setIsLoggedIn} />
                    )}
                </div>
            </header>

            {/* Your other app content */}
        </div>
    );
};

export default App;
