import { useState } from 'react';
import axios from 'axios';

const Login = () => {
    const [password, setPassword] = useState("");
    const [username, setUsername] = useState("");
    const [message, setMessage] = useState("");  // To store messages
    const [token, setToken] = useState(null);    // To store the token after successful login
    const SERVER = 'http://127.0.0.1:8000/';

    // Login function
    const login = () => {
        axios.post(SERVER + "login/", { username, password })
            .then(res => {
                console.log("Response data:", res.data);  // Log the full response data
                const { access } = res.data; // Only destructure the access token
                console.log("Token:", access);   // Log the token
                localStorage.setItem("token", access); // Store token in localStorage
                setToken(access);    // Store the token in state
                setMessage(`Welcome back, ${username}!`); // Show welcome message
            })
            .catch(error => {
                setMessage("Wrong username or password."); // Show error message
                console.log(error); // Log the error for debugging
            });
    };

    // Register function
    const register = () => {
        axios.post(SERVER + "register/", { username, password })
            .then(res => {
                console.log("Response data:", res.data);  // Log the full response data
                const { access } = res.data; // Only destructure the access token
                console.log("Token:", access);   // Log the token
                localStorage.setItem("token", access); // Store token in localStorage
                setToken(access);    // Store the token in state
                setMessage(`Nice to meet you, ${username}!`); // Show registration message
            })
            .catch(error => {
                setMessage("Registration failed. Please try again."); // Error during registration
                console.log(error); // Log the error for debugging
            });
    };

    // Function to check if token exists (e.g., for protected pages)
    const isAuthenticated = () => {
        return localStorage.getItem("token") !== null;
    };

    return (
        <div>
            <div>
                Username: <input onChange={(e) => setUsername(e.target.value)} />
            </div>
            <div>
                Password: <input type="password" onChange={(e) => setPassword(e.target.value)} />
            </div>
            <div>
                <button onClick={login}>Login</button>
                <button onClick={register}>Register</button>
            </div>
            <hr />
            {message && <h2>{message}</h2>} {/* Display the message (either error or welcome message) */}
        </div>
    );
};

export default Login;
