import React, { useState, useEffect } from 'react';

// Define a type for the cart item structure
interface CartItem {
  name: string;
  amount: number;
  totalPrice: number;
}

const CartComponent: React.FC<{ cart: { [key: string]: { name: string, amount: number, totalPrice: number } } }> = ({ cart }) => {
  const cartItems = Object.values(cart);
  const totalSum = cartItems.reduce((sum, item) => sum + item.totalPrice, 0);

  const handleRemoveItem = (name: string) => {
    // You will need a method to remove items from the cart state.
    // The cart can be managed by the parent component, which can update the cart.
  };

  return (
    <div>
      <h2>Shopping Cart</h2>
      {cartItems.length === 0 ? (
        <p>Your cart is empty</p>
      ) : (
        <ul>
          {cartItems.map((item) => (
            <li key={item.name}>
              <h3>{item.name}</h3>
              <p>Amount: {item.amount}</p>
              <p>Price: ${item.totalPrice}</p>
              <button onClick={() => handleRemoveItem(item.name)}>Remove</button>
            </li>
          ))}
        </ul>
      )}
      <div className="total-sum">
        <h3>Total: ${totalSum.toFixed(2)}</h3>
      </div>
    </div>
  );
};

export default CartComponent;
