import React, { useState, useEffect } from 'react';
import CategoriesComponent from './bodyComponents/Categories'; 
import ProductsComponent from './bodyComponents/Products'; 
import Login from './headerComponents/Login'; 
import Logout from './headerComponents/Logout'; 
import { Route, Routes } from 'react-router-dom';
import CartComponent from './bodyComponents/CartComponent';

const App: React.FC = () => {
  const [isLoggedIn, setIsLoggedIn] = useState<boolean>(false);
  const [cart, setCart] = useState<{ [key: string]: { name: string; amount: number; totalPrice: number; } }>({});

  useEffect(() => {
    const token = localStorage.getItem('token');
    setIsLoggedIn(!!token);
  }, []);

  return (
    <div className="App">
      <header>
        <div className="left">
          {isLoggedIn ? (
            <Logout setIsLoggedIn={setIsLoggedIn} />
          ) : (
            <Login setIsLoggedIn={setIsLoggedIn} />
          )}
        </div>
      </header>

      <main>
        <Routes>
          <Route path="/" element={<CategoriesComponent />}>
            {/* Nested Route for products under each category */}
            <Route path="/category/:id" element={<ProductsComponent setCart={setCart} />} />
          </Route>
        </Routes>
      </main>

      <div>
        {/* Pass the cart state as a prop to CartComponent */}
        <CartComponent cart={cart} />
      </div>
    </div>
  );
};

export default App;
