import React, { useState, useEffect } from 'react';
import Login from './headerComponents/Login'; // Import the Login component
import Logout from './headerComponents/Logout'; // Import the Logout component

const App: React.FC = () => {
    const [isLoggedIn, setIsLoggedIn] = useState<boolean>(false);
    const [message, setMessage] = useState<string>(''); // Move the message state here

    useEffect(() => {
        // Check if token exists in local storage
        const token = localStorage.getItem('token');
        setIsLoggedIn(!!token); // Update login state based on the presence of a token
    }, []);

    return (
        <div className="App">
            <header>
                <div className="left">
                    {isLoggedIn ? <Logout /> : <Login setMessage={setMessage} />} {/* Pass setMessage to Login */}
                </div>
                {message && <h2>{message}</h2>} {/* Display the message here */}
            </header>

            {/* The rest of your app */}
        </div>
    );
};

export default App;
