import React, { createContext, useState, useContext, ReactNode } from 'react';

type CartItem = { name: string; amount: number; totalPrice: number };

interface CartContextType {
  cart: { [key: string]: CartItem };
  addToCart: (name: string, price: number, amount: number) => void;
  removeFromCart: (name: string) => void;
}

const CartContext = createContext<CartContextType | undefined>(undefined);

export const CartProvider: React.FC<{ children: ReactNode }> = ({ children }) => {
  const [cart, setCart] = useState<{ [key: string]: CartItem }>({});

  const addToCart = (name: string, price: number, amount: number) => {
    setCart((prevCart) => {
      const newCart = { ...prevCart };
      if (newCart[name]) {
        newCart[name].amount += amount;
        newCart[name].totalPrice += price * amount;
      } else {
        newCart[name] = { name, amount, totalPrice: price * amount };
      }
      return newCart;
    });
  };

  const removeFromCart = (name: string) => {
    setCart((prevCart) => {
      const newCart = { ...prevCart };
      delete newCart[name];
      return newCart;
    });
  };

  return (
    <CartContext.Provider value={{ cart, addToCart, removeFromCart }}>
      {children}
    </CartContext.Provider>
  );
};

export const useCart = (): CartContextType => {
  const context = useContext(CartContext);
  if (!context) {
    throw new Error('useCart must be used within a CartProvider');
  }
  return context;
};
