// App.tsx
import React, { useState, useEffect } from 'react';
import CategoriesComponent from './bodyComponents/Categories'; 
import ProductsComponent from './bodyComponents/Products'; 
import Login from './headerComponents/Login'; 
import Logout from './headerComponents/Logout'; 
import { Route, Routes, Link } from 'react-router-dom';

const App: React.FC = () => {
  const [isLoggedIn, setIsLoggedIn] = useState<boolean>(false);

  useEffect(() => {
    const token = localStorage.getItem('token');
    setIsLoggedIn(!!token);
  }, []);

  return (
    <div className="App">
      <header>
        <div className="left">
          {isLoggedIn ? (
            <Logout setIsLoggedIn={setIsLoggedIn} />
          ) : (
            <Login setIsLoggedIn={setIsLoggedIn} />
          )}
        </div>
      </header>

      <main>
        <Routes>
          {/* Home route - List of Categories */}
          <Route path="/" element={<CategoriesComponent />} />

          {/* Route for displaying products based on category id */}
          <Route path="/category/:id" element={<ProductsComponent />} />
        </Routes>
      </main>
    </div>
  );
};

export default App;
