import React, { useState } from 'react';

const AmountControl: React.FC<{ productName: string, productPrice: number, updateCart: () => void }> = ({ productName, productPrice, updateCart }) => {
  const [amount, setAmount] = useState<number>(0);

  const increaseAmount = () => setAmount(amount + 1);

  const decreaseAmount = () => {
    if (amount > 0) setAmount(amount - 1);
  };

  const addToCart = () => {
    const token = localStorage.getItem('token');
    if (token) {
      const totalPrice = productPrice * amount;
      const cartItem = { name: productName, amount, totalPrice };

      let cart = JSON.parse(localStorage.getItem('cart') || '[]');
      cart.push(cartItem);
      localStorage.setItem('cart', JSON.stringify(cart));

      updateCart();  // This will trigger a re-render and update the cart in CartComponent
    } else {
      alert('Please log in or register to add items to your cart.');
    }
  };

  return (
    <div className="amount-control">
      <div className="amount-display">
        <h3>Amount: {amount}</h3>
      </div>
      <div className="buttons">
        <button onClick={decreaseAmount} disabled={amount === 0}>-</button>
        <button onClick={increaseAmount}>+</button>
      </div>
      <div className="add-to-cart">
        <button onClick={addToCart} disabled={amount <= 0}>Add to Cart</button>
      </div>
    </div>
  );
};

export default AmountControl;
