import React, { useState } from 'react';

const AmountControl: React.FC<{ productName: string, productPrice: number }> = ({ productName, productPrice }) => {
  // State to manage the amount, initially set to 0
  const [amount, setAmount] = useState<number>(0);

  // Function to handle increase
  const increaseAmount = () => {
    setAmount(amount + 1);
  };

  // Function to handle decrease, with a check to ensure it doesn't go below 0
  const decreaseAmount = () => {
    if (amount > 0) {
      setAmount(amount - 1);
    }
  };

  // Function to handle adding to cart
  const addToCart = () => {
    const token = localStorage.getItem('token');  // Check for token in local storage

    if (token) {
      const totalPrice = productPrice * amount;
      
      // Create the cart item object
      const cartItem = {
        name: productName,
        amount,
        totalPrice,
      };

      // Store the cart item in local storage
      let cart = JSON.parse(localStorage.getItem('cart') || '[]');
      cart.push(cartItem);
      localStorage.setItem('cart', JSON.stringify(cart));

      console.log('Added to cart:', cartItem);
    } else {
      alert('Please log in or register to add items to your cart.');
    }
  };

  return (
    <div className="amount-control">
      <div className="amount-display">
        <h3>Amount: {amount}</h3>
      </div>
      <div className="buttons">
        <button
          onClick={decreaseAmount}
          disabled={amount === 0} // Disable minus button when amount is 0
        >
          -
        </button>
        <button onClick={increaseAmount}>+</button>
      </div>
      <div className="add-to-cart">
        <button
          onClick={addToCart}
          disabled={amount <= 0} // Disable "Add to Cart" if amount is 0 or less
        >
          Add to Cart
        </button>
      </div>
    </div>
  );
};

export default AmountControl;
