import React, { useEffect, useState } from 'react';

interface LogoutProps {
    setIsLoggedIn: (status: boolean) => void; // Accept setIsLoggedIn to update login state
}

const Logout: React.FC<LogoutProps> = ({ setIsLoggedIn }) => {
    const [username, setUsername] = useState<string | null>(null);

    useEffect(() => {
        const storedUsername = localStorage.getItem('username'); // Retrieve username from localStorage
        setUsername(storedUsername);
    }, []);

    const handleLogout = () => {
        localStorage.removeItem('token'); // Remove the token from localStorage
        localStorage.removeItem('username'); // Remove the username from localStorage
        setIsLoggedIn(false); // Update login state to false
    };

    return (
        <div>
            {username ? (
                <>
                    <p>Welcome, {username}!</p>
                    <button onClick={handleLogout}>Logout</button>
                </>
            ) : (
                <p>You are not logged in.</p>
            )}
        </div>
    );
};

export default Logout;
