import React, { useState, useEffect } from 'react';
import CategoriesComponent from './bodyComponents/Categories';
import ProductsComponent from './bodyComponents/Products';
import Login from './headerComponents/Login';
import Logout from './headerComponents/Logout';
import { Route, Routes } from 'react-router-dom';
import CartComponent from './bodyComponents/CartComponent';

const App: React.FC = () => {
  const [isLoggedIn, setIsLoggedIn] = useState<boolean>(false);
  const [cartItems, setCartItems] = useState<CartItem[]>([]);  // Lift cart state here
  const [totalSum, setTotalSum] = useState<number>(0);

  useEffect(() => {
    const token = localStorage.getItem('token');
    setIsLoggedIn(!!token);
  }, []);

  // This function will update the cart in both state and localStorage
  const updateCart = (newCart: CartItem[]) => {
    localStorage.setItem('cart', JSON.stringify(newCart));
    setCartItems(newCart);

    // Calculate the total sum after cart update
    const total = newCart.reduce((sum, item) => sum + item.totalPrice, 0);
    setTotalSum(total);
  };

  return (
    <div className="App">
      <header>
        <div className="left">
          {isLoggedIn ? (
            <Logout setIsLoggedIn={setIsLoggedIn} />
          ) : (
            <Login setIsLoggedIn={setIsLoggedIn} />
          )}
        </div>
      </header>

      <main>
        <Routes>
          <Route path="/" element={<CategoriesComponent />} />
          {/* Pass updateCart to ProductsComponent */}
          <Route
            path="/category/:id"
            element={<ProductsComponent updateCart={updateCart} />}
          />
        </Routes>
      </main>

      <div>
        {/* Pass cartItems and totalSum to CartComponent */}
        <CartComponent cartItems={cartItems} totalSum={totalSum} updateCart={updateCart} />
      </div>
    </div>
  );
};

export default App;
