import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { Product } from './ShopModels';
import AmountControl from './AmountControl';

const ProductsComponent: React.FC = () => {
  const [products, setProducts] = useState<Product[]>([]);
  const [cartItems, setCartItems] = useState<any[]>([]);
  const { id } = useParams<{ id: string }>();

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await fetch(`http://127.0.0.1:8000/products?category=${id}`);
        if (response.ok) {
          const data = await response.json();
          setProducts(data);
        } else {
          console.error('Failed to fetch products');
        }
      } catch (error) {
        console.error('Error fetching products:', error);
      }
    };

    if (id) fetchProducts();
  }, [id]);

  // This function will update the cart when an item is added
  const updateCart = () => {
    const cart = JSON.parse(localStorage.getItem('cart') || '[]');
    setCartItems(cart);
  };

  return (
    <div>
      <h2>Products</h2>
      <ul>
        {products.map((product) => (
          <li key={product.id}>
            <h3>{product.name}</h3>
            <p>Price: ${product.price}</p>
            <AmountControl 
              productName={product.name} 
              productPrice={product.price} 
              updateCart={updateCart}  // Pass updateCart function as a prop
            />
          </li>
        ))}
      </ul>
    </div>
  );
};

export default ProductsComponent;
