import React, { useState } from 'react';

interface AmountControlProps {
  productName: string;
  productPrice: number;
  addToCart: (productName: string, productPrice: number, amount: number) => void; // Pass the addToCart function as prop
}

const AmountControl: React.FC<AmountControlProps> = ({ productName, productPrice, addToCart }) => {
  // State to manage the amount, initially set to 0
  const [amount, setAmount] = useState<number>(0);

  // Function to handle increase
  const increaseAmount = () => {
    setAmount(amount + 1);
  };

  // Function to handle decrease, with a check to ensure it doesn't go below 0
  const decreaseAmount = () => {
    if (amount > 0) {
      setAmount(amount - 1);
    }
  };

  // Function to handle adding to cart
  const handleAddToCart = () => {
    if (amount > 0) {
      addToCart(productName, productPrice, amount);  // Add to the cart using parent function
    }
  };

  return (
    <div className="amount-control">
      <div className="amount-display">
        <h3>Amount: {amount}</h3>
      </div>
      <div className="buttons">
        <button onClick={decreaseAmount} disabled={amount === 0}>
          -
        </button>
        <button onClick={increaseAmount}>+</button>
      </div>
      <div className="add-to-cart">
        <button onClick={handleAddToCart} disabled={amount <= 0}>
          Add to Cart
        </button>
      </div>
    </div>
  );
};

export default AmountControl;
