// ProductsComponent.tsx
import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { Product } from './ShopModels';
import AmountControl from './AmountControl';

interface ProductsComponentProps {
  addToCart: (product: Product) => void; // Add product to cart function
}

const ProductsComponent: React.FC<ProductsComponentProps> = ({ addToCart }) => {
  const [products, setProducts] = useState<Product[]>([]);
  const { id } = useParams<{ id: string }>();

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await fetch(`http://127.0.0.1:8000/products?category=${id}`);
        if (response.ok) {
          const data = await response.json();
          setProducts(data);
        } else {
          console.error('Failed to fetch products');
        }
      } catch (error) {
        console.error('Error fetching products:', error);
      }
    };

    if (id) {
      fetchProducts();
    }
  }, [id]);

  return (
    <div>
      <h2>Products</h2>
      <ul>
        {products.map((product) => (
          <li key={product.id}>
            <h3>{product.name}</h3>
            <p>Price: ${product.price}</p>
            <AmountControl
              productName={product.name}
              productPrice={product.price}
              addToCart={() => addToCart(product)} // Add to cart function
            />
          </li>
        ))}
      </ul>
    </div>
  );
};

export default ProductsComponent;
