import React, { useState, useEffect } from 'react';
import CategoriesComponent from './bodyComponents/Categories'; 
import ProductsComponent from './bodyComponents/Products'; 
import Login from './headerComponents/Login'; 
import Logout from './headerComponents/Logout'; 
import { Route, Routes } from 'react-router-dom';
import CartComponent from './bodyComponents/CartComponent';

const App: React.FC = () => {
  const [isLoggedIn, setIsLoggedIn] = useState<boolean>(false);
  const [cart, setCart] = useState<any[]>([]);
  const [username, setUsername] = useState<string | null>(null);

  // Load user data and cart from localStorage on mount
  useEffect(() => {
    const storedToken = localStorage.getItem('token');
    const storedUsername = localStorage.getItem('username');

    if (storedToken) {
      setIsLoggedIn(true);
      setUsername(storedUsername); // Store the username from the token
    }

    // Load the cart from localStorage using the username as the key
    if (storedUsername) {
      const storedCart = localStorage.getItem(`cart_${storedUsername}`);
      if (storedCart) {
        setCart(JSON.parse(storedCart));
      }
    }
  }, []);

  // Store cart to localStorage whenever it changes
  useEffect(() => {
    if (cart.length > 0 && username) {
      localStorage.setItem(`cart_${username}`, JSON.stringify(cart));
    }
  }, [cart, username]);

  const addToCart = (product: any) => {
    setCart((prevCart) => {
      const existingItem = prevCart.find(item => item.id === product.id);
      if (existingItem) {
        return prevCart.map(item =>
          item.id === product.id
            ? { ...item, quantity: item.quantity + 1 }
            : item
        );
      } else {
        return [...prevCart, { ...product, quantity: 1 }];
      }
    });
  };

  const increaseQuantity = (id: number) => {
    setCart((prevCart) =>
      prevCart.map(item =>
        item.id === id
          ? { ...item, quantity: item.quantity + 1 }
          : item
      )
    );
  };

  const decreaseQuantity = (id: number) => {
    setCart((prevCart) =>
      prevCart.map(item =>
        item.id === id && item.quantity > 1
          ? { ...item, quantity: item.quantity - 1 }
          : item
      )
    );
  };

  const removeItem = (id: number) => {
    setCart((prevCart) => prevCart.filter(item => item.id !== id));
  };

  // Function to clear the cart (and remove it from localStorage)
  const clearCart = () => {
    setCart([]); // Clear cart from state
    if (username) {
      localStorage.removeItem(`cart_${username}`); // Clear cart from localStorage by username
    }
  };

  return (
    <div className="App">
      <header>
        <div className="left">
          {isLoggedIn ? (
            <Logout setIsLoggedIn={setIsLoggedIn} setUsername={setUsername} />
          ) : (
            <Login setIsLoggedIn={setIsLoggedIn} setUsername={setUsername} />
          )}
        </div>
      </header>

      <main>
        <Routes>
          <Route path="/" element={<CategoriesComponent />} />
          {/* Nested route for products */}
          <Route
            path="category/:id"
            element={<ProductsComponent addToCart={addToCart} />}
          />
        </Routes>
      </main>

      <CartComponent
        cartItems={cart}
        increaseQuantity={increaseQuantity}
        decreaseQuantity={decreaseQuantity}
        removeItem={removeItem}
        clearCart={clearCart} // Pass clearCart to CartComponent
      />
    </div>
  );
};

export default App;
